import React from "react"
import {InputMoment, BigInputMoment, DatePicker, TimePicker} from 'react-input-moment';
import moment from 'moment'

class App extends React.Component {
  state = {
    name: ""
  }
  

  constructor(props) {
    super(props);
    this.state = {
      startTime: 0,
      endTime: 0,
      totalPay: 0,
      preBedTimePay: 0,
      postBedTimePay: 0,
      postMidnightHours: 0,
    };
    this.sayHello = this.sayHello.bind(this);
    this.updateStartTime = this.updateStartTime.bind(this)
    this.updateEndTime = this.updateEndTime.bind(this)
    this.updatePay = this.updatePay.bind(this);
  }

  updateStartTime(event) {
    this.setState({startTime: event.target.value})
  }

  updateEndTime(event) {
    this.setState({endTime: event.target.value})
  }

  sayHello(startTime,endTime, bedTime) {
      let hoursWorked = endTime.diff(startTime, 'hours')
      let midnightTime = moment().add(1, 'days').hour(0).minute(0).second(0)
      let totalPay = 0
      let preBedTimePay = 0
      let postBedTimePay = 0
      let postMidnightPay = 0
  
      if (endTime > bedTime) {
          let hoursOfWork = bedTime.diff(startTime, 'hours')
          preBedTimePay = hoursOfWork * 12
          totalPay += preBedTimePay
          console.log({totalPay, preBedTimePay, hoursOfWork})
  
          if (endTime > midnightTime ) {
              let preMidnightHours = midnightTime.diff(bedTime, 'hours')
              postBedTimePay = preMidnightHours * 8
              totalPay += postBedTimePay
              console.log({totalPay, postBedTimePay, preMidnightHours})
  
              let postMidnightHours = endTime.diff(midnightTime, 'hours')
              postMidnightPay = postMidnightHours * 16
              totalPay += postMidnightPay
              console.log({totalPay, postMidnightPay, postMidnightHours})
  
  
          } else {
              let preMidnightHours = endTime.diff(bedTime, 'hours')
              postBedTimePay = preMidnightHours * 8
              totalPay += postBedTimePay
              console.log({totalPay, postBedTimePay, preMidnightHours})
              
          }
  
  
      } else {
          let hoursOfWork = endTime.diff(startTime, 'hours')
          preBedTimePay = hoursOfWork * 12
          totalPay += preBedTimePay
          console.log({totalPay, preBedTimePay, hoursOfWork})
          alert(totalPay);

          return {
              hoursWorked: hoursOfWork,
              preBedTimePay: preBedTimePay, 
              postBedTimePay: 0,
              postMidnightPay: 0,
              totalPay: totalPay
          }
      }
  
      alert(totalPay);
      
      return {
          hoursWorked: hoursWorked,
          preBedTimePay: preBedTimePay, 
          postBedTimePay: postBedTimePay,
          postMidnightPay: postMidnightPay,
          totalPay: totalPay
      }
  }

  componentDidMount() {
    fetch("http://localhost:3000")
      .then(res => res.json())
      .then(data => this.setState({ name: data.name }))
  }

  render() {
    return (
      <body>
      <h1>Hello {this.state.name}!</h1>
      <label for="appt">Choose a time for your meeting:</label>

      <input type="time" id="startTime" name="startTime" min="0:00" max="23:59" required></input>
      <input type="time" id="endTime" name="endTime" min="0:00" max="23:59" required></input>

      <button onClick={this.sayHello()}>
      Click me!
      </button>

      
      <small>start time cannot be before 5pm and end time cannot be later than 4am</small>

      </body>
    )
  }
}

export default App
